import { Component, OnInit } from '@angular/core';
import { ProfileService } from '../_services/profile.service';
import { Router } from '@angular/router';
import { Skill } from '../models/skill';
import { Certificate } from '../models/certificate';
import { Profile } from '../models/profile';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit {
  profile: Profile | null = null;
  selectedFile: File | null = null;
  isEditing = false;
  isAddingSkill = false;
  newSkill = '';
  isAddingCertificate = false;
  newCertificate = '';
  newCertificateIssuer = '';
  errorMessage = '';

  constructor(private profileService: ProfileService, private router: Router) { }

  ngOnInit(): void {
    this.fetchProfile();
  }

  fetchProfile(): void {
    this.profileService.getProfile().subscribe(
      (data) => {
        this.profile = data;

        if (this.profile) {
          // Safely handle skills
          if (Array.isArray(this.profile.skills)) {
            this.profile.skills = this.profile.skills.map((skill: Skill) => skill.name);
          } else {
            this.profile.skills = [];
          }

          // Safely handle certificates
          if (Array.isArray(this.profile.certificates)) {
            this.profile.certificates = this.profile.certificates.map((certificate: Certificate) => ({
              name: certificate.name,
              issuer: certificate.issuer
            }));
          } else {
            this.profile.certificates = [];
          }

          console.log('Fetched profile:', this.profile);
        }
      },
      (error) => {
        this.errorMessage = 'Error fetching profile data';
        console.error(this.errorMessage, error);
      }
    );
  }


  toggleAddForm(formType: 'skill' | 'certificate') {
    if (formType === 'skill') {
      this.isAddingSkill = !this.isAddingSkill;
      this.newSkill = '';
    } else {
      this.isAddingCertificate = !this.isAddingCertificate;
      this.newCertificate = '';
    }
  }

  onAddSkill() {
    if (this.newSkill) {
      this.profileService.addSkill(this.newSkill).subscribe(
        response => {
          console.log('Skill added successfully:', response);
          this.fetchProfile();
          this.newSkill = '';
          this.isAddingSkill = false;
        },
        error => {
          this.errorMessage = 'Error adding skill';
          console.error(this.errorMessage, error);
        }
      );
    }
  }

  onAddCertificate() {
    if (this.newCertificate) {
      this.profileService.addCertificates(this.newCertificate, this.newCertificateIssuer).subscribe(
        response => {
          console.log('Certificate added successfully:', response);
          this.fetchProfile();
          this.newCertificate = '';
          this.isAddingCertificate = false;
        },
        error => {
          this.errorMessage = 'Error adding certificate';
          console.error(this.errorMessage, error);
        }
      );
    }
  }
}
<div class="background-image">
  <div class="profile-container" *ngIf="profile">
    <div class="profile-details">
      <h2>User Profile</h2>
      <div *ngIf="!isEditing">
        <p><strong>Name:</strong> {{ profile.name }}</p>
        <p><strong>Phone:</strong> {{ profile.phone }}</p>
        <p><strong>Email:</strong> {{ profile.email }}</p>
        <p><strong>Age:</strong> {{ profile.age }}</p>
        <p><strong>Address:</strong> {{ profile.address }}</p>
        <button (click)="toggleEdit()">Edit Profile</button>
        <button (click)="toggleAddSkill()">Add Skills</button>
        <button (click)="toggleAddCertificate()">Add Certificates</button>
      </div>
      <div *ngIf="isEditing" class="edit-form">
        <input [(ngModel)]="profile.name" placeholder="Name" />
        <input [(ngModel)]="profile.phone" placeholder="Phone" />
        <input [(ngModel)]="profile.email" placeholder="Email" />
        <input [(ngModel)]="profile.age" placeholder="Age" />
        <input [(ngModel)]="profile.address" placeholder="Address" />
        <button (click)="updateProfile()">Save Changes</button>
        <button class="cancel" (click)="toggleEdit()">Cancel</button>
      </div>

      <div *ngIf="isAddingSkill" class="add-skill-form">
        <input [(ngModel)]="newSkill" placeholder="Add a skill" />
        <button (click)="onAddSkill()">Add</button>
        <button class="cancel" (click)="toggleAddSkill()">Cancel</button>
      </div>

      <div class="skills-list" *ngIf="profile.skills && profile.skills.length">
        <h3>Skills:</h3>
        <ul>
          <li *ngFor="let skill of profile.skills">{{ skill }}</li>
        </ul>
      </div>

      <div *ngIf="isAddingCertificate" class="add-certificate-form">
        <input [(ngModel)]="newCertificate" placeholder="Add a certificate" />
        <input [(ngModel)]="newCertificateIssuer" placeholder="Add a certificate Issuer" />
        <button (click)="onAddCertificate()">Add</button>
        <button class="cancel" (click)="toggleAddCertificate()">Cancel</button>
      </div>
      <div class="certificate-list" *ngIf="profile.certificates && profile.certificates.length">
        <h3>Certificates:</h3>
        <ul>
          <li *ngFor="let certificate of profile.certificates">{{ certificate }}</li>
        </ul>
      </div>
    </div>
    <div class="work-experience">
      <app-work-experience></app-work-experience>
    </div>
  </div>
  <div *ngIf="!profile">
    <p>No user data available.</p>
  </div>
</div>
