import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AccountService } from '../_services/account.service';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {
  constructor(private accountService: AccountService, private router: Router) { }

  isLoggedIn(): boolean {
    return this.accountService.isLoggedIn(); // Implement this method in your AuthService
  }

  logout() {
    this.accountService.logout(); // Implement this method to handle logout
    this.router.navigate(['/login']); // Redirect to login page after logout
  }
}
<nav>
  <ul>
    <li><a routerLink="/profile">View Profile</a></li>
    <li><a routerLink="/workexperience">View/Add Work Experience</a></li>
    <li><a (click)="logout()">Logout</a></li>
  </ul>
</nav>
