import { Component, OnInit } from '@angular/core';
import { ProfileService } from '../_services/profile.service';
import { Router } from '@angular/router';
import { Skill } from '../models/skill';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit {
  profile: any;
  selectedFile: File | null = null;
  isEditing: boolean = false;
  isAddingSkill: boolean = false;
  newSkill: string = '';
  isAddingCertificate: boolean = false;
  newCertificateName: string = '';
  newCertificateIssuer: string = '';

  constructor(private profileService: ProfileService, private router: Router) { }

  ngOnInit(): void {
    this.fetchProfile();
  }

  fetchProfile(): void {
    this.profileService.getProfile().subscribe(
      (data) => {
        this.profile = data;

        // Check if skills have values and extract them
        if (this.profile.skills && this.profile.skills.$values) {
          this.profile.skills = this.profile.skills.$values.map((skill: Skill) => skill.name); 
        } else {
          this.profile.skills = []; // Ensure skills is an array
        }

        console.log('Fetched profile:', this.profile);
      },
      (error) => {
        console.error('Error fetching profile data', error);
      }
    );
  }

  // Toggle edit mode
  toggleEdit() {
    this.isEditing = !this.isEditing;
  }

  // Update the profile details
  updateProfile() {
    const updatedData = {
      name: this.profile.name,
      phone: this.profile.phone,
      email: this.profile.email,
      age: this.profile.age,
      address: this.profile.address
    };

    this.profileService.updateProfile(updatedData).subscribe(
      response => {
        console.log('Profile updated successfully', response);
        this.isEditing = false; // Exit edit mode after successful update
        this.fetchProfile(); // Refresh the profile data
      },
      error => {
        console.error('Error updating profile', error);
      }
    );
  }

  // Open file input for editing the profile picture
  editProfilePicture() {
    const fileInput = document.createElement('input');
    fileInput.type = 'file';
    fileInput.accept = 'image/*';
    fileInput.onchange = (event) => {
      const target = event.target as HTMLInputElement;
      if (target.files) {
        this.selectedFile = target.files[0];
        this.uploadProfilePicture();
      }
    };
    fileInput.click();
  }

  // Upload the selected profile picture
  uploadProfilePicture() {
    if (this.selectedFile) {
      this.profileService.uploadProfilePicture(this.selectedFile).subscribe(
        response => {
          console.log('Profile picture uploaded successfully', response);
          this.fetchProfile(); // Fetch updated profile to reflect new picture URL
        },
        error => {
          console.error('Error uploading profile picture', error);
        }
      );
    }
  }

  // Navigate to the work experience section
  navigateToWorkExperience() {
    this.router.navigate(['/workexperience']);
  }

  // Toggle adding skill form
  toggleAddSkill() {
    this.isAddingSkill = !this.isAddingSkill;
    this.newSkill = ''; // Clear input when toggling
  }

  // Add a new skill
  onAddSkill() {
    if (this.newSkill) {
      this.profileService.addSkill(this.newSkill).subscribe(
        response => {
          console.log('Skill added successfully:', response);
          this.fetchProfile(); // Refresh profile to show updated skills
          this.newSkill = ''; // Clear skill input
          this.isAddingSkill = false; // Close add skill form
        },
        error => {
          console.error('Error adding skill:', error);
        }
      );
    }
  }
  // Toggle adding certificate form
  toggleAddCertificate() {
    this.isAddingCertificate = !this.isAddingCertificate;
    this.newCertificateName = ''; // Clear input when toggling
    this.newCertificateIssuer = ''; // Clear input when toggling
  }

  // Add a new certificate
  onAddCertificate() {
    if (this.newCertificateName && this.newCertificateIssuer) {
      const certificateDto = {
        name: this.newCertificateName,
        issuer: this.newCertificateIssuer
      };

      this.profileService.addCertificate(certificateDto).subscribe(
        response => {
          console.log('Certificate added successfully:', response);
          this.fetchProfile(); // Refresh profile to show updated certificates
          this.newCertificateName = ''; // Clear input
          this.newCertificateIssuer = ''; // Clear input
          this.isAddingCertificate = false; // Close add certificate form
        },
        error => {
          console.error('Error adding certificate:', error);
        }
      );
    }
  }
}
<div class="background-image">
  <div class="profile-container" *ngIf="profile">
    <div class="profile-details">
      <h2>User Profile</h2>
      <div *ngIf="!isEditing">
        <p><strong>Name:</strong> {{ profile.name }}</p>
        <p><strong>Phone:</strong> {{ profile.phone }}</p>
        <p><strong>Email:</strong> {{ profile.email }}</p>
        <p><strong>Age:</strong> {{ profile.age }}</p>
        <p><strong>Address:</strong> {{ profile.address }}</p>
        <button (click)="toggleEdit()">Edit Profile</button>
        <button (click)="toggleAddSkill()">Add Skills</button>
        <button (click)="toggleAddCertificate()">Add Certificate</button>
      </div>

      <div *ngIf="isEditing" class="edit-form">
        <input [(ngModel)]="profile.name" placeholder="Name" />
        <input [(ngModel)]="profile.phone" placeholder="Phone" />
        <input [(ngModel)]="profile.email" placeholder="Email" />
        <input [(ngModel)]="profile.age" placeholder="Age" />
        <input [(ngModel)]="profile.address" placeholder="Address" />
        <button (click)="updateProfile()">Save Changes</button>
        <button class="cancel" (click)="toggleEdit()">Cancel</button>
      </div>

      <div *ngIf="isAddingSkill" class="add-skill-form">
        <input [(ngModel)]="newSkill" placeholder="Add a skill" />
        <button (click)="onAddSkill()">Add</button>
        <button class="cancel" (click)="toggleAddSkill()">Cancel</button>
      </div>

      <div *ngIf="isAddingCertificate" class="add-certificate-form">
        <input [(ngModel)]="newCertificateName" placeholder="Certificate Name" />
        <input [(ngModel)]="newCertificateIssuer" placeholder="Issuer" />
        <button (click)="onAddCertificate()">Add Certificate</button>
        <button class="cancel" (click)="toggleAddCertificate()">Cancel</button>
      </div>

      <!-- Skills and Certificates Table -->
      <div class="table-container">
        <h3>Skills and Certificates</h3>
        <table class="skills-certificates-table">
          <thead>
            <tr>
              <th>Skills</th>
              <th>Certificates</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let skill of profile.skills; let i = index">
              <td [ngClass]="{'odd-row': i % 2 !== 0}">{{ skill }}</td>
              <td [ngClass]="{'odd-row': i % 2 !== 0}">
                <ng-container *ngIf="profile.certificates && profile.certificates.$values && profile.certificates.$values[i]">
                  {{ profile.certificates.$values[i].name }} - {{ profile.certificates.$values[i].issuer }}
                </ng-container>
              </td>
            </tr>
            <ng-container *ngIf="profile.certificates.$values.length > profile.skills.length">
              <tr *ngFor="let certificate of profile.certificates.$values; let j = index">
                <td [ngClass]="{'odd-row': (j + profile.skills.length) % 2 !== 0}"></td>
                <td [ngClass]="{'odd-row': (j + profile.skills.length) % 2 !== 0}">{{ certificate.name }} - {{ certificate.issuer }}</td>
              </tr>
            </ng-container>
          </tbody>
        </table>
      </div>
    </div>
    <div class="work-experience">
      <app-work-experience></app-work-experience>
    </div>
  </div>
  <div *ngIf="!profile">
    <p>No user data available.</p>
  </div>
</div>
