import { Component, OnInit } from '@angular/core';
import { ProfileService } from '../_services/profile.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit {
  profile: any;
  selectedFile: File | null = null;

  constructor(private profileService: ProfileService, private router: Router) { }

  ngOnInit(): void {
    this.fetchProfile();
  }

  // Fetch the user profile data from the server
  fetchProfile(): void {
    this.profileService.getProfile().subscribe(
      (data) => {
        this.profile = data;
      },
      (error) => {
        console.error('Error fetching profile data', error);
      }
    );
  }

  // Update the profile details
  updateProfile() {
    const updatedData = {
      name: this.profile.name,
      phone: this.profile.phone,
      email: this.profile.email,
      age: this.profile.age,
      address: this.profile.address // Ensure this field exists in your profile object
    };

    this.profileService.updateProfile(updatedData).subscribe(
      response => {
        console.log('Profile updated successfully', response);
        // Optionally, you can show a success message to the user
      },
      error => {
        console.error('Error updating profile', error);
        // Optionally, show an error message to the user
      }
    );
  }

  // Open file input for editing the profile picture
  editProfilePicture() {
    const fileInput = document.createElement('input');
    fileInput.type = 'file';
    fileInput.accept = 'image/*';
    fileInput.onchange = (event) => {
      const target = event.target as HTMLInputElement;
      if (target.files) {
        this.selectedFile = target.files[0];
        this.uploadProfilePicture();
      }
    };
    fileInput.click();
  }

  // Upload the selected profile picture
  uploadProfilePicture() {
    if (this.selectedFile) {
      this.profileService.uploadProfilePicture(this.selectedFile).subscribe(
        response => {
          console.log('Profile picture uploaded successfully', response);
          // Fetch updated profile to ensure the new picture URL is reflected
          this.fetchProfile();
        },
        error => {
          console.error('Error uploading profile picture', error);
          // Optionally, show an error message to the user
        }
      );
    }
  }

  // Navigate to the work experience section
  navigateToWorkExperience() {
    this.router.navigate(['/workexperience']);
  }
}
<div class="background-image">
  <div class="profile-container" *ngIf="profile">
    <div class="profile-details">
      <h2>User Profile</h2>
      <div class="profile-picture">
        <img *ngIf="profile.pictureUrl" [src]="profile.pictureUrl" alt="Profile Picture" />
        <button (click)="editProfilePicture()">Edit</button>
      </div>
      <p><strong>Name:</strong> {{ profile.name }}</p>
      <p><strong>Phone:</strong> {{ profile.phone }}</p>
      <p><strong>Email:</strong> {{ profile.email }}</p>
      <p><strong>Age:</strong> {{ profile.age }}</p>
      <button (click)="navigateToWorkExperience()">Add Work Experience</button>
    </div>
    <div class="work-experience">
      <app-work-experience></app-work-experience>
    </div>
  </div>
</div>
