import { Component, OnInit } from '@angular/core';
import { ProfileService } from '../profile.service'; // Import the WorkExperience model
import { WorkExperience } from '../models/work-experience';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit {
  profile: any;
  workExperiences: WorkExperience[] = [];
  selectedFile: File | null = null;

  constructor(private profileService: ProfileService) { }

  ngOnInit(): void {
    this.fetchProfile();
    this.fetchWorkExperiences();
  }

  fetchProfile(): void {
    this.profileService.getProfile().subscribe(
      (data) => {
        this.profile = data;
      },
      (error) => {
        console.error('Error fetching profile data', error);
      }
    );
  }

  fetchWorkExperiences(): void {
    this.profileService.getWorkExperiences().subscribe(
      (data: WorkExperience[]) => {
        this.workExperiences = data;
      },
      (error) => {
        console.error('Error fetching work experiences', error);
      }
    );
  }

  onFileChange(event: any): void {
    this.selectedFile = event.target.files[0];
  }

  submitWorkExperience(): void {
    if (this.selectedFile) {
      this.profileService.importWorkExperience(this.selectedFile).subscribe(
        (response) => {
          console.log('Work experiences imported successfully:', response);
          this.fetchWorkExperiences(); // Refresh work experiences after import
        },
        (error) => {
          console.error('Error importing work experiences:', error);
        }
      );
    } else {
      console.warn('No file selected for import.');
    }
  }
}
<div class="profile-container" *ngIf="profile">
  <h2>User Profile</h2>
  <p><strong>Name:</strong> {{ profile.name }}</p>
  <p><strong>Phone:</strong> {{ profile.phone }}</p>
  <p><strong>Email:</strong> {{ profile.email }}</p>
  <p><strong>Age:</strong> {{ profile.age }}</p>

  <h3>Import Work Experience</h3>
  <input type="file" (change)="onFileChange($event)" accept=".csv" />
  <button (click)="submitWorkExperience()">Import</button>
</div>

<div *ngIf="!profile">
  <p>No user data available.</p>
</div>
<app-work-experience [workExperiences]="workExperiences"></app-work-experience>
