<div class="registration-container">
  <h2>Register</h2>
  <form [formGroup]="registrationForm" (ngSubmit)="onSubmit()">
    <div>
      <label>Name</label>
      <input formControlName="name" type="text" />
      <div *ngIf="submitted && formControls.name.errors" class="error">
        Name is required.
      </div>
    </div>

    <div>
      <label>Age</label>
      <input formControlName="age" type="number" />
      <div *ngIf="submitted && formControls.age.errors" class="error">
        Age is required and must be greater than 0.
      </div>
    </div>

    <div>
      <label>Email</label>
      <input formControlName="email" type="email" />
      <div *ngIf="submitted && formControls.email.errors" class="error">
        Valid email is required.
      </div>
    </div>

    <div>
      <label>Phone</label>
      <input formControlName="phone" type="tel" />
      <div *ngIf="submitted && formControls.phone.errors" class="error">
        Phone is required.
      </div>
    </div>

    <div>
      <label>Password</label>
      <input formControlName="password" type="password" />
      <div *ngIf="submitted && formControls.password.errors" class="error">
        Password is required and must be at least 6 characters.
      </div>
    </div>

    <button type="submit">Register</button>
  </form>
</div>
