import { Component, Input } from '@angular/core';
import { WorkExperience } from '../models/work-experience';

@Component({
  selector: 'app-work-experience',
  templateUrl: './work-experience.component.html',
  styleUrls: ['./work-experience.component.css']
})
export class WorkExperienceComponent {
  @Input() workExperiences: WorkExperience[] = [];
}
<div *ngIf="workExperiences.length > 0">
  <h3>Work Experience</h3>
  <ul>
    <li *ngFor="let experience of workExperiences">
      <strong>Job Title:</strong> {{ experience.jobTitle }} <br>
      <strong>Company Name:</strong> {{ experience.companyName }} <br>
      <strong>Duration:</strong> {{ experience.duration }}
    </li>
  </ul>
</div>
<div *ngIf="workExperiences.length === 0">
  <p>No work experience available.</p>
</div>
